import {useState} from "react"

import './App.css';
import Box from './component/Box';


const choice ={
  rock:{
    name:"Rock",
    img:"https://nationaltoday.com/wp-content/uploads/2021/08/National-Pet-Rock-Day-1200x834.jpg"
  },
  scissors:{
    name:"Scissors",
    img:"https://cafe24.poxo.com/ec01/bibon1/jy78sT5iv9X+V5IHQBaKwrjoUZw7/nGXjx58SNFF4bK6NrHp9v6yW73e7/DyTNtncYBJmFI45IbmtzNo91A3rQ==/_/web/product/big/202101/833fa88eae073a583699cc737f0b8a91.jpg"
  },
  paper:{
    name:"Paper",
    img:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfAj6mQwx4OrtR0e6cO9VGfDHSBN900tkOaw&usqp=CAU"
  }
}


function App() {

  const [pick, pickChange] = useState(null)
  const [comPick, comPickChange] = useState(null)
  const [result, setResult] = useState("");

  const play =(userChoice)=>{
    pickChange(choice[userChoice])

    let comChoice =randomChoice()
    comPickChange(comChoice)

    setResult(judgement(choice[userChoice], comChoice))

  }

  const judgement = (user, computer)=>{
    if (user.name == computer.name){
        return "same"
    } else if (user.name == "Rock") return computer.name == "scissors" ? "win" : "lose"
    else if (user.name == "Scissors") return computer.name == "Paper" ? "win" : "lose"
    else if (user.name == "Paper") return computer.name == "Rock" ? "win" : "lose"
    }


 const randomChoice =()=>{
  let itemArray = Object.keys(choice)
  let randomItem = Math.floor(Math.random()*itemArray.length);
  let final= itemArray[randomItem]
  return choice[final]
 }

  return (
    <div>
    <div className='main'>
      <Box title="컴퓨터" item={comPick} result={result}/>
      <Box title="나" item={pick} result={result}/>
   </div>
   <div className='button-click'>
    <button onClick={()=>play("scissors")}>가위</button>
    <button onClick={()=>play("rock")}>바위</button>
    <button onClick={()=>play("paper")}>보</button>
   </div>


   </div>
  );
}

export default App;
